import { Component, OnInit } from '@angular/core';
import { SaleService } from '../services/sale.service';
import { Router } from '@angular/router';
import { SaleModel } from '../models/sale.model';
import { ShowingModel } from '../models/showing.model';
import { ShowingService } from '../services/showing.service';

@Component({
  selector: 'app-list-sales',
  templateUrl: './list-sales.component.html',
  styleUrls: ['./list-sales.component.css']
})
export class ListSalesComponent implements OnInit{

  constructor (private saleService : SaleService, private showingService: ShowingService, 
    private router : Router){

  }

  sales : SaleModel[] = [];
  show: ShowingModel = new ShowingModel();

  ngOnInit(): void {

    //this.FullColumns();

    try{

      this.saleService.getSales().subscribe(data=>{

        this.sales = data; 


    });


    }catch(e){
      console.log("error: "+ e);
    }

  }

  public getInfoShow(id:Number):ShowingModel{

    this.showingService.getShowingById(id).subscribe(data=>{
      this.show = data;
    });

    return this.show;

  }

}
<div>
    <label>Showing Selection</label>
        <br>
        <select (change)="onChange()"  [(ngModel)]="selectedItem">
            <option *ngFor="let item of showings" [value]="item.showID">{{ item.movieName }}</option>
        </select>
</div>
<div>
    <table>
        <thead>
            <tr>
                <th>Date</th>
                <th>Movie Name</th>
                <th>Schedule</th>
                <th>No. Tickets</th>
                <th>Total Price</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let s of sales">
                <td>{{s.date}}</td>
                <td>movieName</td>
                <td>schedule</td>
                <td>{{s.tickets.length}}</td>
                <td>{{s.saleValue}}</td>
            </tr>
        </tbody>
    </table>
</div>
