import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'frontend-cinema';
}
<div class="header">
  <h1><img src=".\assets\images\cinemalogo.jpg"></h1>
  <nav>
    <ul>      
        <li><a routerLink="create-showing" routerLinkActive="active">Create Showing</a></li>
        <li><a routerLink="list-showing" routerLinkActive="active">List Showing</a></li>
        <li><a routerLink="create-sale" routerLinkActive="active">Create Sale</a></li>
        <li><a routerLink="list-sales" routerLinkActive="active">List Sale</a></li>
    </ul>
  </nav>
</div>

<div class="container">
  <router-outlet></router-outlet>
</div>
