import { Component } from '@angular/core';
import { ScheduleService } from '../services/schedule.service';
import { ScheduleModel } from '../models/schedule.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-manage-discount',
  templateUrl: './manage-discount.component.html',
  styleUrls: ['./manage-discount.component.css']
})
export class ManageDiscountComponent {

  hour!:number;
  minutes!:number;
  disCheck: boolean = false;
  discount!:number;
  selectedSchedule!:Number;
  schedules!: ScheduleModel[];
  currentDisc!:number;
  discountUp!:number;

  constructor(private scheduleService: ScheduleService, private router: Router) {


  }

  onSubmitCreate(){
    if(this.validateSubmit()){

      let schedule = new ScheduleModel();
      schedule.hour = this.hour;
      schedule.minutes = this.minutes;
      if(this.disCheck){
        schedule.isDiscount=true;
        schedule.discount = this.discount;
      }

      this.scheduleService.postSchedule(schedule).subscribe(d=>{
        window.alert("successful id: "+d.scheduleID);
      });

      this.router.navigateByUrl('/',{skipLocationChange:true}).then(()=>{
        this.router.navigate(['manage-discount']).then(()=>{
          console.log(`After navigation I am on:${this.router.url}`)
        })
      });

    }else{
      window.alert("you can't leave fields blank");
    }
  }

  onSubmitUpdate(){

  }

  public check(event: any){

    const myCheckbox = document.getElementById("isDisc") as HTMLInputElement;
    this.disCheck = myCheckbox.checked;

    console.log("check: "+this.disCheck);

  }

  private validateSubmit():boolean{
    let flag = false;

    if(this.hour!=null && this.minutes!=null){
      if(this.hour!=0){
        if(this.disCheck){
          if(this.discount!=null){
            flag=true
          }
        }else{
          flag=true
        }
      }
    }

    return flag;
  }

}
<div class="box">
    <div class="create">
        <form #form="ngForm" (ngSubmit)="onSubmitCreate()">
            <label>HOUR:</label>
            <input class="sch" type="number" [(ngModel)]="hour" [ngModelOptions]="{ standalone: true }">
            <br>
            <label>MINUTES:</label>
            <input class="sch" type="number" [(ngModel)]="minutes" [ngModelOptions]="{ standalone: true }">

            <input type="checkbox" id="isDisc" (change)="check($event)">
            <label for="isDisc">discount?</label>

            <div [style.display]="disCheck ? 'block' : 'none'">
                <label>DISCOUNT %</label>
                <input class="sch" type="number" [(ngModel)]="discount" [ngModelOptions]="{ standalone: true }">
            </div>
            <br>
            <input type="submit" value="Create" class="btn-create">
        </form>
    </div>
    <div class="update">
        <form #form="ngForm" (ngSubmit)="onSubmitUpdate()">
            <fieldset>
                <legend>CREATE SCHEDULE</legend>

                <label>SCHEDULE:</label>
                <select [(ngModel)]="selectedSchedule" [ngModelOptions]="{ standalone: true }">
                    <option *ngFor="let item of schedules" [value]="item.scheduleID">{{item.hour}}:{{item.minutes}}</option>
                </select>
                <p>%{{currentDisc}}</p>
                <br>
                <label>DISCOUNT %</label>
                <input class="sch" type="number" [(ngModel)]="discountUp" [ngModelOptions]="{ standalone: true }">
                <br>
                <input type="submit" value="Update" class="btn-create">
            </fieldset>
        </form>
    </div>
</div>
