import { Component } from '@angular/core';

@Component({
  selector: 'app-manage-discount',
  templateUrl: './manage-discount.component.html',
  styleUrls: ['./manage-discount.component.css']
})
export class ManageDiscountComponent {

  hour!:number;
  minutes!:number;
  disCheck: boolean = false;

  onSubmit(){

  }

  public check(event: any){

    const myCheckbox = document.getElementById("isDisc") as HTMLInputElement;
    this.disCheck = myCheckbox.checked;

    console.log("check: "+this.disCheck);

  }

}
<div class="box">
    <div class="create">
        <form #form="ngForm" (ngSubmit)="onSubmit()">
            <label>HOUR:</label>
            <input id="hor" type="number" [(ngModel)]="hour" [ngModelOptions]="{ standalone: true }">
            <br>
            <label>MINUTES:</label>
            <input id="min" type="number" [(ngModel)]="minutes" [ngModelOptions]="{ standalone: true }">

            <input type="checkbox" id="isDisc" (change)="check($event)">
            <label for="isDisc">discount?</label>

            <div [style.display]="disCheck ? 'block' : 'none'">
                <label>DISCOUNT %</label>
                <input type="number">
            </div>
            <br>
            <input type="submit" value="Create" class="btn-create">
        </form>
    </div>
    <div class="update">

    </div>
</div>
